CREATE TABLE CLIENT ( CLIENT_ID int not null primary key generated always as identity (start with 1, increment by 1), NAME VARCHAR(255),    PASSWORD VARCHAR(255),    USERNAME VARCHAR(255) NOT NULL,  CLIENT_TYPE INT, ADDRESS VARCHAR(255), DOB DATE, IS_VERIFIED CHAR FOR BIT DATA, IS_ACTIVE CHAR FOR BIT DATA);
CREATE TABLE APPOINTMENT (APPOINTMENT_ID int not null primary key generated always as identity (start with 1, increment by 1), HOST_ID INT references CLIENT(CLIENT_ID), PATIENT_ID INT references CLIENT(CLIENT_ID), COMMENTS VARCHAR(255), APPOINTMENT_START DATE, APPOINTMENT_DURATION TIME, CHARGE FLOAT, IS_COMPLETE CHAR FOR BIT DATA); 